import React, { useState } from 'react';
import ReviewForm from '../components/ReviewForm';
import KakaoMap from '../components/KakaoMap';

function WritePage() {
  const [latestReview, setLatestReview] = useState(null);  // ‚¨Ö Î¶¨Î∑∞ Ï†ÄÏû•

  const handleSubmit = async (reviewData) => {
    console.log('üì¶ ÌîÑÎ°†Ìä∏ÏóêÏÑú Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞:', reviewData);
    setLatestReview(reviewData);  // ‚¨Ö ÏßÄÎèÑÏóê Î∞òÏòÅ

    try {
      const response = await fetch('http://localhost:4000/api/reviews', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(reviewData),
      });

      if (response.ok) {
        alert('Î¶¨Î∑∞Í∞Ä Îì±Î°ùÎêòÏóàÏäµÎãàÎã§!');
      } else {
        console.error('‚ùå ÏùëÎãµ Ïã§Ìå®:', response.status);
      }
    } catch (error) {
      console.error('üî• Î¶¨Î∑∞ Îì±Î°ù Ïã§Ìå®:', error);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Î¶¨Î∑∞ ÏûëÏÑ±</h1>
      <ReviewForm onSubmit={handleSubmit} />

      <h2 className="mt-8 text-xl font-semibold">üìç ÏßÄÎèÑÏóêÏÑú Î≥¥Í∏∞</h2>
      {latestReview && <KakaoMap records={[latestReview]} />}  {/* ‚úÖ Ïó¨Í∏∞ ÌïµÏã¨ */}
    </div>
  );
}

export default WritePage;
