import React, { useState } from 'react';

function ReviewForm({ onSubmit }) {
  const [form, setForm] = useState({
    place: '',
    address: '',
    category: '',
    supportPrice: '',
    visitDate: '',
    paymentPrice: '',
    blogLink: '',
    menuType: '',
    isComplete: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // üîß Ïà´Ïûê ÌïÑÎìú Î≥ÄÌôò: ÎπÑÏñ¥ ÏûàÏúºÎ©¥ 0 Ï≤òÎ¶¨
    const parsedForm = {
      ...form,
      supportPrice: Number(form.supportPrice || 0),
      paymentPrice: Number(form.paymentPrice || 0),
    };

    // ‚úÖ ÎîîÎ≤ÑÍπÖ Î°úÍ∑∏
    console.log('üìù Ï†úÏ∂úÎêú Îç∞Ïù¥ÌÑ∞:', parsedForm);

    onSubmit(parsedForm);
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-wrap gap-2">
      <input name="place" value={form.place} onChange={handleChange} placeholder="Í∞ÄÍ≤åÎ™Ö" className="border p-1" required />
      <input name="address" value={form.address} onChange={handleChange} placeholder="Ï£ºÏÜå" className="border p-1" required />
      <input name="category" value={form.category} onChange={handleChange} placeholder="ÏóÖÏ¢Ö (Ïòà: ÎÉâÏÇº)" className="border p-1" required />
      <input name="menuType" value={form.menuType} onChange={handleChange} placeholder="ÏùåÏãù Ï¢ÖÎ•ò (Ïòà: ÌïúÏãù)" className="border p-1" required />
      
      {/* Ïà´Ïûê ÏûÖÎ†• ÌïÑÎìú: type="number" */}
      <input type="number" name="supportPrice" value={form.supportPrice} onChange={handleChange} placeholder="ÏßÄÏõêÍ∏à" className="border p-1" required />
      <input type="number" name="paymentPrice" value={form.paymentPrice} onChange={handleChange} placeholder="Í≤∞Ï†ú Í∏àÏï°" className="border p-1" required />

      <input name="visitDate" value={form.visitDate} onChange={handleChange} placeholder="Ï≤¥ÌóòÏùº (Ïòà: 5Ïõî)" className="border p-1" required />
      <input name="blogLink" value={form.blogLink} onChange={handleChange} placeholder="Î∏îÎ°úÍ∑∏ ÎßÅÌÅ¨" className="border p-1" required />

      <label className="flex items-center">
        <input type="checkbox" name="isComplete" checked={form.isComplete} onChange={handleChange} className="mr-1" />
        ÏôÑÎ£å Ïó¨Î∂Ä
      </label>

      <button type="submit" className="bg-gray-700 text-white px-3 py-1">Îì±Î°ù</button>
    </form>
  );
}

export default ReviewForm;
