import React, { useEffect, useRef } from 'react';

const KakaoMap = ({ records = [] }) => {
  const mapRef = useRef(null);
  const markersRef = useRef([]);

  useEffect(() => {
    const initMap = () => {
      const container = document.getElementById('map');
      if (!container) return;

      const map = new window.kakao.maps.Map(container, {
        center: new window.kakao.maps.LatLng(36.5, 127.5),
        level: 12,
      });

      mapRef.current = map;
    };

    if (!window.kakao || !window.kakao.maps) {
      const script = document.createElement('script');
      script.src = '//dapi.kakao.com/v2/maps/sdk.js?appkey=eb46d70dcce4c3347f6e67a7afcfa896&autoload=false&libraries=services';
      script.async = true;
      script.onload = () => window.kakao.maps.load(initMap);
      document.head.appendChild(script);
    } else {
      window.kakao.maps.load(initMap);
    }
  }, []);

  useEffect(() => {
    if (!window.kakao || !mapRef.current) return;

    const map = mapRef.current;
    const geocoder = new window.kakao.maps.services.Geocoder();
    const bounds = new window.kakao.maps.LatLngBounds();

    // ✅ 중복 주소 제거 (주소가 같으면 1번만 마커 찍음)
    const uniqueRecords = Array.from(
      new Map(records.map((r) => [r.address, r])).values()
    );

    // 이전 마커 제거
    markersRef.current.forEach((marker) => marker.setMap(null));
    markersRef.current = [];

    const geocodePromises = uniqueRecords.map((record) => {
      return new Promise((resolve) => {
        const address = record.address?.trim();
        if (!address) return resolve(null);

        geocoder.addressSearch(address, (result, status) => {
          if (status === window.kakao.maps.services.Status.OK) {
            const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);
            const marker = new window.kakao.maps.Marker({
              map,
              position: coords,
            });

            markersRef.current.push(marker);
            bounds.extend(coords);
            resolve(coords);
          } else {
            console.warn("❌ 주소 검색 실패:", address);
            resolve(null);
          }
        });
      });
    });

    Promise.all(geocodePromises).then((results) => {
      const valid = results.filter((r) => r !== null);
      console.log("✅ 마커 찍힌 개수:", valid.length);

      if (valid.length > 0) {
        map.setBounds(bounds); // 마커 기준으로 자동 확대
      }
    });
  }, [records]);

  return <div id="map" style={{ width: '100%', height: '500px' }} />;
};

export default KakaoMap;
