import React, { useEffect } from 'react';

const KakaoMap = ({ records = [] }) => {
  useEffect(() => {
    const initMap = () => {
      const container = document.getElementById('map');
      if (!container) {
        console.error('β map containerλ¥Ό μ°Ύμ§€ λ»ν–μµλ‹λ‹¤.');
        return;
      }

      const options = {
        center: new window.kakao.maps.LatLng(36.5, 127.5),
        level: 12,
      };

      const map = new window.kakao.maps.Map(container, options);
      const geocoder = new window.kakao.maps.services.Geocoder();

      let first = true; // β” μ²μ ν• λ²λ§ center μ΄λ™

      records.forEach((record) => {
        if (record.address) {
          geocoder.addressSearch(record.address, (result, status) => {
            if (status === window.kakao.maps.services.Status.OK) {
              const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);

              // β” λ§μ»¤ μƒμ„±
              const marker = new window.kakao.maps.Marker({
                map,
                position: coords,
              });

              // β” μΈν¬μλ„μ° μ¶”κ°€ (μ„ νƒ)
              const infowindow = new window.kakao.maps.InfoWindow({
                content: `<div style="padding:5px;font-size:12px;">${record.place || record.address}</div>`,
              });
              window.kakao.maps.event.addListener(marker, 'mouseover', () => infowindow.open(map, marker));
              window.kakao.maps.event.addListener(marker, 'mouseout', () => infowindow.close());

              // β” λ§¨ μ²μ λ§μ»¤μ—λ§ μ¤‘μ‹¬ μ΄λ™
              if (first) {
                map.setCenter(coords);
                first = false;
              }
            } else {
              console.warn('β μ£Όμ† κ²€μƒ‰ μ‹¤ν¨:', record.address);
            }
          });
        }
      });
    };

    const scriptId = 'kakao-map-script';
    const existingScript = document.getElementById(scriptId);

    if (!window.kakao && !existingScript) {
      const script = document.createElement('script');
      script.id = scriptId;
      script.src =
        '//dapi.kakao.com/v2/maps/sdk.js?appkey=eb46d70dcce4c3347f6e67a7afcfa896&autoload=false&libraries=services';
      script.async = true;
      script.onload = () => {
        console.log('π“¦ Kakao μ¤ν¬λ¦½νΈ λ΅λ“λ¨');
        window.kakao.maps.load(initMap);
      };
      document.head.appendChild(script);
    } else if (window.kakao && window.kakao.maps) {
      console.log('π“¦ Kakao μ¤ν¬λ¦½νΈ μ΄λ―Έ λ΅λ“λ¨');
      window.kakao.maps.load(initMap);
    }
  }, [records]);

  return <div id="map" style={{ width: '100%', height: '400px' }}></div>;
};

export default KakaoMap;
