import React, { useEffect, useState } from 'react';
import ReviewForm from '../components/ReviewForm';
import KakaoMap from '../components/KakaoMap';

function WritePage() {
  const [reviews, setReviews] = useState([]);

  // üöÄ ÌéòÏù¥ÏßÄ ÎßàÏö¥Ìä∏ Ïãú Î™®Îì† Î¶¨Î∑∞ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const res = await fetch('http://localhost:4000/api/reviews');
        const data = await res.json();
        setReviews(data); // ‚úÖ Ï†ÑÏ≤¥ Î¶¨Î∑∞ stateÎ°ú Ï†ÄÏû•
      } catch (err) {
        console.error('Î¶¨Î∑∞ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
      }
    };

    fetchReviews();
  }, []);

  // ‚úçÔ∏è Î¶¨Î∑∞ Îì±Î°ù Ìï∏Îì§Îü¨
  const handleSubmit = async (reviewData) => {
    console.log('üì¶ ÌîÑÎ°†Ìä∏ÏóêÏÑú Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞:', reviewData);

    try {
      const response = await fetch('http://localhost:4000/api/reviews', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(reviewData),
      });

      if (response.ok) {
        alert('Î¶¨Î∑∞Í∞Ä Îì±Î°ùÎêòÏóàÏäµÎãàÎã§!');
        // ‚úÖ ÏÉà Î¶¨Î∑∞Î•º reviews Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä
        setReviews((prev) => [...prev, reviewData]);
      } else {
        console.error('‚ùå ÏùëÎãµ Ïã§Ìå®:', response.status);
      }
    } catch (error) {
      console.error('üî• Î¶¨Î∑∞ Îì±Î°ù Ïã§Ìå®:', error);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Î¶¨Î∑∞ ÏûëÏÑ±</h1>
      <ReviewForm onSubmit={handleSubmit} />

      <h2 className="mt-8 text-xl font-semibold">üìç ÏßÄÎèÑÏóêÏÑú Î≥¥Í∏∞</h2>
      {/* ‚úÖ Ïù¥Ï†ú Î™®Îì† Î¶¨Î∑∞Î•º ÏßÄÎèÑÏóê ÌëúÏãú */}
      {reviews.length > 0 && <KakaoMap records={reviews} />}
    </div>
  );
}

export default WritePage;
