import React, { useEffect } from "react";

const KakaoMap = ({ records = [] }) => {
  useEffect(() => {
    // Ïù¥ÎØ∏ kakao Ïä§ÌÅ¨Î¶ΩÌä∏Í∞Ä Î°úÎî©ÎêòÏñ¥ ÏûàÎäî Í≤ΩÏö∞, Ï§ëÎ≥µ Î°úÎî© Î∞©ÏßÄ
    if (window.kakao && window.kakao.maps) {
      loadMap();
    } else {
      const script = document.createElement("script");
      script.src =
        "//dapi.kakao.com/v2/maps/sdk.js?appkey=cca675112191f29282761d3066b280e1&autoload=false&libraries=services";
      script.async = true;

      script.onload = () => {
        window.kakao.maps.load(loadMap);
      };

      document.head.appendChild(script);
    }

    function loadMap() {
      const container = document.getElementById("map");
      if (!container) {
        console.error("‚ùå 'map' DOM ÏöîÏÜåÍ∞Ä Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏäµÎãàÎã§.");
        return;
      }

      const options = {
        center: new window.kakao.maps.LatLng(36.0, 127.5), // Ï†ÑÍµ≠ Ï§ëÏã¨ Ï¢åÌëú
        level: 12,
      };

      const map = new window.kakao.maps.Map(container, options);
      const geocoder = new window.kakao.maps.services.Geocoder();

      records.forEach((record) => {
        if (record.address) {
          geocoder.addressSearch(record.address, (result, status) => {
            if (status === window.kakao.maps.services.Status.OK) {
              const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);

              const marker = new window.kakao.maps.Marker({
                map,
                position: coords,
              });

              const infowindow = new window.kakao.maps.InfoWindow({
                content: `<div style="font-size:12px;">${record.store}</div>`,
              });

              marker.addListener("click", () => {
                infowindow.open(map, marker);
              });
            } else {
              console.warn(`‚ö†Ô∏è Ï£ºÏÜå Í≤ÄÏÉâ Ïã§Ìå®: ${record.address}`);
            }
          });
        }
      });
    }
  }, [records]);

  return (
    <div>
      <h2 className="text-xl font-bold mt-6 mb-2">üìç ÏßÄÎèÑÏóêÏÑú Î≥¥Í∏∞</h2>
      <div
        id="map"
        style={{ width: "100%", height: "400px", border: "1px solid #ccc" }}
      ></div>
    </div>
  );
};

export default KakaoMap;
